uniform sampler2D sp;uniform float pi;uniform vec3 so;uniform vec4 lo;uniform vec4 il;uniform vec4 ps;uniform vec3 es;uniform bool drawPath;varying vec4 hc;varying vec3 cc;varying vec3 hh;void main(){float a=pi*0.01;gl_FragColor.rgb=hc.rgb;gl_FragColor.a=1.0;if(!drawPath){
#ifdef GLOBAL
gl_FragColor=(texture2D(sp,gl_PointCoord)*gl_FragColor)-vec4(0.15,0.15,0.15,0.0);
#else
gl_FragColor=(texture2D(sp,gl_PointCoord)*gl_FragColor)+vec4(0.27,0.27,0.27,0.0);
#endif
gl_FragColor.a*=a;}else{gl_FragColor.a*=(0.3*a);}if(gl_FragColor.r<=1e-6&&gl_FragColor.g<=1e-6&&gl_FragColor.b<=1e-6){gl_FragColor.rgb=vec3(0.1,0.1,0.1);gl_FragColor.a=0.1;}}

attribute vec3 $auxpos1;uniform mat4 se;uniform mat4 pp;uniform sampler2D lm;uniform vec2 ss;uniform sampler2D le;uniform vec2 sm;uniform float ol;uniform float pm;uniform float si;uniform vec2 oe;uniform bool drawPath;uniform sampler2D ls;uniform bool ms;varying vec4 hc;varying vec3 cc;varying vec3 hh; $linearInterpolator  $lonlat2position  $localTrans  $curveInter void main(void){float a=fract($auxpos1.x*2.0/sm.x);float b=floor($auxpos1.x*2.0/sm.y)/sm.y;vec4 c=texture2D(le,vec2(a,b));if(abs(c.r-$auxpos1.x)<Epsilon){vec3 d=c.gba;a=fract(($auxpos1.x*2.0+1.0)/sm.x);b=floor(($auxpos1.x*2.0+1.0)/sm.y)/sm.y;vec4 e=texture2D(le,vec2(a,b));vec3 f=e.gba;vec3 g,h,i,j;float k,l;float m=1.0;float n=0.0;
#ifdef GLOBAL
d=lonlat2position(d);f=lonlat2position(f);g=(d+f)*0.5;h=normalize(g);i=d-f;k=length(i);g=h*(length(g)+k*0.6);if(k<=1000.0){l=32.0;}else if(k<=10000.0){l=24.0;}else if(k<=500000.0){l=18.0;}else if(k<=1000000.0){l=40.0;}else{l=floor(k*0.00001);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/ol*si,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.6*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}cc=normalize(j);
#else
d=wgs84ToWebMerc(d);f=wgs84ToWebMerc(f);g=(d+f)*0.5;h=vec3(0.0,0.0,1.0);i=d-f;k=length(i);g=g+h*(k*0.3);if(k<=1000.0){l=48.0;}else if(k<=10000.0){l=42.0;}else if(k<=100000.0){l=32.0;}else if(k<=1000000.0){l=24.0;}else if(k<=2000000.0){l=36.0;}else{l=floor(k*0.000006);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/ol*si,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.3*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}cc=vec3(0.0,0.0,1.0);
#endif
gl_Position=se*pp*vec4(j,1.0);hh=j;a=fract($auxpos1.y/ss.x);b=floor($auxpos1.y/ss.y)/ss.y;float r=(texture2D(lm,vec2(a,b))).r;float s=getLinearValue(oe,r);if(!drawPath){gl_PointSize=pm*pow(2.718,s);}hc=texture2D(ls,vec2(s,0.5));}else{vec3 j=vec3(0.0,0.0,0.0);gl_Position=se*pp*vec4(j,1.0);hh=j;if(!drawPath) gl_PointSize=pm;hc=vec4(1.0,1.0,0.0,1.0);cc=normalize(j);}}
