uniform vec3 is;uniform vec4 ll;uniform vec4 so;uniform vec4 ii;uniform vec3 os;uniform float sl;uniform vec3 ie;varying vec2 dh;varying vec3 ch;varying vec3 hd;varying vec4 hh;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(hd-os);vec3 e=normalize(ch);vec3 f=normalize(reflect(d,e));float g=max(dot(f,is),.001);vec3 h=c*ii.rgb*ii.w*pow(g,16.0);vec3 i=a*so.rgb*so.w*clamp(dot(e,is),.0,1.0);vec3 j=b*ll.rgb*ll.w;vec3 k=vec3(j+i+h);float l=abs(dh.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(ie,1.0)+l*hh;gl_FragColor*=vec4(k,1.0);float m=sl*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 se;uniform mat4 ms;uniform bool mo;uniform float em;uniform sampler2D ei;uniform sampler2D ss;uniform vec2 lm;uniform float mm;uniform vec2 ps;uniform float el;uniform vec2 ls;uniform bool pl;uniform sampler2D es;varying vec3 ch;varying vec3 hd;varying vec2 dh;varying vec4 hh; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/lm.x);float f=floor($auxpos1.x/lm.y)/lm.y;float g=(texture2D(ei,vec2(e,f))).r;float h=(texture2D(ss,vec2(e,f))).r;float i=mod(el,mm);float j=getExpoEaseOutValue(i,g,h,mm);if(pl) j=h;float k=getLinearValue(ls,j);if(mo){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(ps,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(ps,k);}if($auxpos1.y==a) dh=vec2(0.0,0.0);else if($auxpos1.y==d) dh=vec2(0.0,1.0);else if($auxpos1.y==b) dh=vec2(1.0,0.0);else if($auxpos1.y==c) dh=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=se*ms*vec4(o,1.0);ch=normalize(p);hd=o;hh=texture2D(es,vec2(k,0.5));}else{float t=getScope(ps,k);mat4 r=getTransMat($position);float u=($auxpos1.y-em/2.0)*(20.0/em);float v=($auxpos1.z-em/2.0)*(20.0/em);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(ps,k);vec3 o=(r*vec4(t*($auxpos1.y/em-0.5),t*($auxpos1.z/em-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=se*ms*vec4(o,1.0);ch=p;hd=o;dh=$auxpos1.yz/(em-1.0);}}
